import React, { Component } from 'react';
import Modal from 'react-modal';
import { Link } from 'react-router-dom';

export default class FullBlogPost extends Component {
  constructor(props){
    super(props);
    const {title, body} = props.location.state;
    this.state={
      modalIsOpen: false,
      title: title,
      body: body,
      newTitle: "",
      newBody: ""
    }
  }

  handleModal = ()=> {
    this.setState({modalIsOpen: !this.state.modalIsOpen});
  }

  handleChange1 = (text) => {
    title = text;
  }

  updatePost = () =>{
    this.setState({
      title: this.state.newTitle,
      body: this.state.newBody,
      modalIsOpen: false
    });
  }

  render() {
    return(
      <div>
        <Link to="./"> back </Link>
        <h1>{this.state.title}</h1>
        <p>{this.state.body}</p>
        <button onClick={this.handleModal}>Edit This Post</button>
        <Modal
          isOpen={this.state.modalIsOpen}
          onRequestClose={this.modal}
          contentLabel="Example Modal">
          <input type="text" onChange={ this.handleChange1 } value={ this.state.title }/>
          <input type="text" onChange={ this.handleChange } value={ this.state.body }/>
          <button onClick={this.updatePost} > Save Changes </button>
          <button onClick={this.handleModal} > Cancel </button>
        </Modal>
      </div>
    );
  }
}
