import React, { Component } from 'react';
import Modal from 'react-modal';
import { Link } from 'react-router-dom';

export default class FullBlogPost extends Component {
  constructor(props){
    super(props);
    const {title, body} = props.location.state;
    this.state={
      modalIsOpen: false,
      title: title,
      body: body,
      newTitle: "",
      newBody:""
    }
  }

  modal = ()=> {
    this.setState({modalIsOpen: !this.state.modalIsOpen});
  }

  handleChange = (text) => {
    this.setState({newTitle: text.target.value});
  } 

  render() {
    return(
      <div>
        <Link to="./"> back </Link>
        <h1>{this.state.title}</h1>
        <p>{this.state.body}</p>
        <button onClick={this.openModal}>Edit This Post</button>
        <Modal
          isOpen={this.state.modalIsOpen}
          onRequestClose={this.closeModal}
          contentLabel="Example Modal">
          <input type="text" onChange={ this.handleChange } value={ this.state.title }/>
          <input type="text" onChange={ this.handleChange } value={ this.state.body }/>
          <button onClick > Save Changes </button>
          <button onClick={this.modal} > Cancel </button>
        </Modal>
      </div>
    );
  }
}
