import React, { Component } from 'react';
import { Link } from 'react-router-dom';

export default class FullBlogPost extends Component {

  componentDidMount(){
    const { blogpost } = this.props.location.state;
  }

  render() {
    return(
      <div>
        <Link to="./"> back </Link>
        <h1>{this.blogpost}</h1>
        <p>{this.props.body}</p>
        <button>Edit This Post</button>
      </div>
    );
  }
}
